import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";

export default function NavBar() {
  const myState = useSelector((state) => state.savingData);
  const [data, setData] = useState([]);

  useEffect(() => {
    setData(myState);
  }, [data]);
log
  return (
    <>
      <div
        style={{
          height: "5%",
          width: "94%",
          border: "2px solid black",
          margin: "1%",
          borderRadius: "10px",
        }}
      >
        <ul style={{ display: "flex" }}>
          <li style={{ marginLeft: "5%", listStyle: "none" }}>Home</li>
          <li style={{ marginLeft: "5%", listStyle: "none" }}>Mobile</li>
          <li style={{ marginLeft: "5%", listStyle: "none" }}>Laptop</li>
          <li style={{ marginLeft: "5%", listStyle: "none" }}>Clothings</li>
        </ul>
      </div>
      <div
        style={{
          height: "100px",
          width: "100%",
          display: "flex",
          flexWrap: "wrap",
          border: "2px solid black",
        }}
      >
        {!data ? console.log("data") : data &&
          data.map((d, i) => {
            return (
              <div
                key={i}
                style={{
                  border: "solid 2px black",
                  height: "100px",
                  width: "100px",
                  margin: "1%",
                  borderRadius: "100px",
                }}
              >
                <img
                  src={d.image}
                  alt=""
                  style={{
                    height: "100px",
                    width: "100px",
                    marginLeft: "0%",
                    marginTop: "0%",
                  }}
                />
              </div>
            );
          }) }
      </div>
    </>
  );
}
