import React from 'react';
i

export default function Cart() {


  return (
    <div style={{height: "100%",width: "94%"}}>
        {
          myState.rj.map((d,i)=>{
            return(
              <div key={i} style={{marginLeft : "3%" , marginTop : "1%" , display : "flex"}}>
                <img  src={d.image} alt="noImage" style={{ height: "500px", width: "500px"}} />
                <div style={{width:"20%"  , marginLeft: "30%"}}>
                <h1 style={{marginTop : "15%"}}>{d.title}</h1>
                <p>{d.description}</p>
                <p>Category : {d.category}</p>
                <p>Rating {d.rating.rate}</p>
                <p>Price {d.price} ETH </p>
                <div style={{display : "flex" , flexDirection : "row"}}>
                <button onClick={()=>{dispatch(dispatch(decNum()))}} style={{height : "30px" , width : "30px"}}>-</button>
                <h2 style={{marginTop:"-0.2%" , marginLeft : "1%" , marginRight : "1%"}}>{myState2}</h2>
                <button onClick={()=>{dispatch(dispatch(incNum()))}} style={{height : "30px" , width : "30px"}}>+</button>
                </div>
                <p>Total Cost = {d.price*myState2}</p>
                </div>
              </div>
            )
          })
        }
      </div>
  )
}
