const initialState = { rj: [] };

const savingData = (state = initialState, action) => {
  switch (action.type) {
    case "savingData":
      return { ...state, rj : [...state.rj, action.data] };
    case "deleteData" :
      const apps = state.rj;
      const itemToBeRemoved = action.data;
      console.log(itemToBeRemoved);
      apps.splice(apps.findIndex(a => a.id === itemToBeRemoved.id) , 1)

      return { ...state, rj : [...state.rj, action.data]}
    default:
      return state;
  }
};
export default savingData;
