// import { increaseQty } from "../actions/index";
import {useDispatch} from "react-redux";

export const incNum = () => {
  return { type: "Increment" };
};

export const decNum = () => {
  return { type: "Decrement" };
};

export const savingCartData = (data) => {
  const dispatch = useDispatch();
  dispatch(increaseQty(data));
  return {
    type : "savingCartData" ,
    data : {...data , qty : 1}
  }

}

export const deleteCartData = (data) => {

  return {
    type : "deleteCartData" ,
    data : data
  }

}



export const savingAPI = (data) => {

  return {
    type : "savingAPI" ,
    data : data
  }

}

export const increaseQty = (data) => {
  return {
    type : "increaseQty" ,
    qty : data.qty ,
    id : data.id
  }
}

export const decreaseQty = (data) => {
  return {
    type : "decreaseQty" ,
    qty : data.qty ,
    id : data.id
  }
}
