const initialState = { rj: [] };

const savingData = (state = initialState, action) => {
  switch (action.type) {
    case "savingData":
      return { ...state, rj : [...state.rj, action.data] };
    case "deleteData" :
      // action.data.pop();
      let a = state.rj.filter((d)=>d.id)
      console.log(state.rj);
      // console.log(action.data.id);
      return { ...state, rj : [...state.rj, action.data]}
    default:
      return state;
  }
};
export default savingData;
