import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

export default function SignUp() {

    function handlingInputs(e){
        console.log(e.target.name , " = " , e.t);
    }
    return (
        <div>
            <Form method='POST' className="mx-auto" style={{width: '400px' , margin : "7%"}}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>First Name</Form.Label>
                    <Form.Control onChange={handlingInputs} name = "firstName" type="text" placeholder="Enter First Name" />
                    <Form.Label>Last Name</Form.Label>
                    <Form.Control name = "lastName" type="text" placeholder="Enter Last Name" />
                    <Form.Label>Email address</Form.Label>
                    <Form.Control name = "email" type="email" placeholder="Enter email" />
                    <Form.Text className="text-muted">
                        We'll never share your email with anyone else.
                    </Form.Text>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control name = "password" type="password" placeholder="Password" />
                    <Form.Label>Re-Enter Password</Form.Label>
                    <Form.Control name = "rePassword" type="password" placeholder="Re-Enter Password" />
                </Form.Group>
                <Button style={{display : "block" , margin : "auto"}}  variant="success" type="submit">
                    Submit
                </Button>
            </Form>
        </div>
    )
}
