const initialState = { rj: [] };

const cartData = (state = initialState, action) => {
  switch (action.type) {

    case "savingCartData" :
      // state.rj = state.rj.filter((d)=>{ if(d.id === action.data.id){check = true } return d.id !== action.data.id});
      // if(check === true ){state.rj[].id}
      let obj = state.rj.find((o, i) => {
        if (o.id === action.id) {
            state.rj[i].id = state.rj[i].id++;
            return true; // stop searching
        }
      });
      console.log(o);
      return { ...state, rj : [...state.rj, action.data]  };

    case "deleteCartData" :
      state.rj = state.rj.filter((ele) => {return ele.id !== action.data})
      return { ...state, rj : [...state.rj]}

    default:
      return state;
  }
};
export default cartData;
