  return (
    <div style={{height: "100%",width: "94%",display: "flex",flexWrap: "wrap",margin : "auto"}}>
    {
      a.filter(filtering).map((data,index)=>{
        <div key={index} style={{height: "350px", width: "350px" , display : "block" , margin : "auto" , marginTop : "1%" }} >
