            <p style={{textAlign : "center"}}>Price {data.price} ETH</p>
            <button style={{ display : "block" , margin : "auto" }} onClick={()=>dispatch(savingData(data))}>Add To Cart</button>
            <p>{data.id} .</p>
          </div>
      })
    }
  </div>
  );
}
