import { useEffect, useState } from "react";

export default function Product() {
  const [product, setProduct] = useState([{}]);
  useEffect(() => {
    const fetchProducts = async () => {
      const res = await fetch("https://fakestoreapi.com/products");
      const data = await res.json();
      console.log(data);
      setProduct(data);
    };
    fetchProducts();
  }, []);

  return (
    <div style={{ height : "100%" , width : "100%" , display : "flex" , flexWrap : "wrap" }}>
      {product.map((data, index) => {
        return (
          <div
            style={{border : "solid 2px grey" , height: "400px", width: "400px" , margin : "1%"}}
          >
            <img
              src={data.image}
              alt=""
              style={{ height: "300px", width: "300px" , marginLeft : ""}}
            />
            <p>
              <center>Price {data.price}</center>
            </p>
            <button style={{ marginLeft: "40%" }}>Add To Cart</button>
          </div>
        );
      })}
    </div>
  );
}
