const initialState = { rj: [] };

const cartData = (state = initialState, action) => {
  switch (action.type) {

    case "savingCartData" :
      let check =false ;
      state.rj = state.rj.filter((d)=>{ if(d.id === action.data.id){d.id = state.id+1} return d.id !== action.data.id});
      // if(check === true ){}
      return { ...state, rj : [...state.rj, action.data]  };

    case "deleteCartData" :
      state.rj = state.rj.filter((ele) => {return ele.id !== action.data})
      return { ...state, rj : [...state.rj]}

    default:
      return state;
  }
};
export default cartData;
