const initialState = { rj: [] };

const cartData = (state = initialState, action) => {
  switch (action.type) {
    case "savingCartData" :
      let check = false;
      state.rj = state.rj.filter((d)=>{ if(d.id === action.data.id){check = true } 
      if(check === true ){d.qty = a.qt+1}
      return d.id !== action.data.id});
      console.log(action.data , check);
      return { ...state, rj : [...state.rj, action.data]  };

    case "deleteCartData" :
      state.rj = state.rj.filter((ele) => {return ele.id !== action.data})
      return { ...state, rj : [...state.rj]}

    default:
      return state;
  }
};
export default cartData;
